import React from 'react'

export const BackendErrorsMsg = ({ errors }) => {
    console.log('BackendErrorsMsg', errors);
    const errorsMsgs = Object.keys(errors).map(name => {
        return name + ' ' + errors[name].join(' ');
    });
    return (
        <ul>
            {
                errorsMsgs.map(el => <li>{el}</li>)
            }
        </ul>
    )
}
