import React, {useState, useEffect, useRef, useContext} from 'react';
import {NavLink, Link} from 'react-router-dom';

import { CurrentUserContext } from 'context';

export const Topbar = () => {

  let [cntClick, setCntClick] = useState(1);
  let [{currentUser, isLoggedIn}] = useContext(CurrentUserContext);
  console.log('User top menu', currentUser);

  // Refs не отслеживаются реактом
  let renderCnt = useRef(1);

  useEffect(() => {
    renderCnt.current++;
  });

  return (
    <nav className="navbar navbar-light">
      <div className="container">
        <Link to="/" className="navbar-brand">
          Medium
        </Link>
        <ul className="nav navbar-nav pull-xs-right">
          <li className="nav-item">
            <NavLink to="/" className="nav-link" exact>
              Home
            </NavLink>
          </li>
          <li className="nav-item" onClick={ () => setCntClick(++cntClick) }>
            <NavLink to="#" className="nav-link" exact >
              Render component cnt = {renderCnt.current} state change cnt = {cntClick}
            </NavLink>
          </li>
          {
            isLoggedIn ? 
            (
              <>
                <li className="nav-item">
                  <NavLink to={`/profile/${ currentUser.username}`} className="nav-link">
                    Profile / {currentUser.email}
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/article/new" className="nav-link">
                    Article new
                  </NavLink>
                </li>
              </>
            ) 
            :
            (
              <>
                <li className="nav-item">
                  <NavLink to="/login" className="nav-link">
                    Sign in
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/register" className="nav-link">
                    Sign up
                  </NavLink>
                </li>
              </>
            )
          }

        </ul>
      </div>
    </nav>
  )
}
